import React from "react";
import TANKS_DATA from "./constant"

function Tank(props) {
  return (
    <div className="container">
      <button
        className="button"
        onClick={() => {
          props.setDirection(TANKS_DATA[props.index].dir);
        }}
      >
        {TANKS_DATA[props.index].button}
      </button>
      <div className="tank">
        <div
          id="fill"
          className="fill"
          style={{
            height: `${(props.tank * 300) / TANKS_DATA[props.index].volume}px`
          }}
        />
      </div>

      <div
        className="label"
        style={{
          position: `relative`,
          left: `${110}px`,
          top: `${-(props.tank * 300) / TANKS_DATA[props.index].volume}px`
        }}
      >
        {props.tank}
      </div>
      <div>{`${props.tank} of ${TANKS_DATA[props.index].volume}`}</div>
    </div>
  );
}

export default React.memo(Tank);
